#include <iostream>
#include <vector>
#include <string>
#include <deque>
#include <sstream>
#include <algorithm>

using namespace std;

vector<int> findSubstring(string s, vector<string>& words) 
{
	//1.将字符串所有可能组合生成一个vector
	//2.BM_BC 从后往前遍历
	//3.复杂度 ！words.size * n

	if (s.empty()|| words.empty()) return vector<int>();
	if (s.size() < words.size()) return vector<int>();
	if (s.size() == words[0].size() && words.size() == 1 && s == words[0]) return vector<int>{0};

	vector<int> indexs;
	vector<string> good_string;
	vector<string> x_temp;
	string x;

	int len = words[0].size() * words.size();

	// 遍历words寻找所有合法 position
	for (auto word : words)
	{
		x_temp.push_back(word);

		deque<string> temp;
		auto pos = s.find(word);
		if (pos == string::npos) return vector<int>();

		while (pos != string::npos && pos <= (s.size() - len))
		{
			
			int n = 0;
			while (n < words.size())
			{
				temp.push_back(string(s, pos + n * words[0].size(), words[0].size()));
				n++;
			}
			if (!temp.empty())
			{
				sort(temp.begin(), temp.end());
				stringstream ss;
				while (!temp.empty())
				{
					ss << temp.front();
					temp.pop_front();
				}
				good_string.push_back(ss.str());
			}
			indexs.push_back(pos);
			pos = s.find(word, pos + 1);
		}
	}

	sort(x_temp.begin(), x_temp.end());
	stringstream sx;
	for (auto a : x_temp)
		sx << a;

	x = sx.str();

	vector<int> resault;
	for (int i = 0;i<good_string.size();i++)
		if (good_string[i] == x)
			resault.push_back(indexs[i]);

	sort(resault.begin(), resault.end());
	auto z = unique(resault.begin(), resault.end());
	resault.erase(z, resault.end());
	


	return resault;
}

